dataSet.query:
SELECT ALLOWED InventoryBalances.ProductsAndServices AS ProductsAndServices, InventoryBalances.Characteristic AS Characteristic, InventoryBalances.QuantityBalance AS QuantityBalance INTO TemporaryTableAvailableBallance FROM AccumulationRegister.Inventory.Balance(, CustomerOrder = VALUE(Document.CustomerOrder.EmptyRef) {(ProductsAndServices).* AS ProductsAndServices, (StructuralUnit).* AS WarehouseForBalances, (Company).* AS Company}) AS InventoryBalances
INDEX BY ProductsAndServices, Characteristic ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED ProductsAndServices.Ref AS ProductsAndServices, ProductsAndServicesCharacteristics.Ref AS Characteristic, ProductsAndServices.DeletionMark AS DeletionMark, ProductsAndServices.Parent AS Parent, ProductsAndServices.IsFolder AS IsFolder, ProductsAndServices.Code AS Code, ProductsAndServices.Description AS Description, ProductsAndServices.SKU AS SKU, ProductsAndServices.ProductsAndServicesType AS ProductsAndServicesType, ProductsAndServices.MeasurementUnit AS MeasurementUnit, ProductsAndServices.VATRate AS VATRate, ProductsAndServices.PictureFile AS PictureFile, ProductsAndServicesCharacteristics.Description AS CharacteristicDescription, ProductsAndServicesCharacteristics.DeletionMark AS CharacteristicDeletionMark INTO TemporaryTableProductsAndServicesAndCharacteristics FROM Catalog.ProductsAndServices AS ProductsAndServices INNER JOIN Catalog.ProductsAndServicesCharacteristics AS ProductsAndServicesCharacteristics ON (ProductsAndServicesCharacteristics.Owner = ProductsAndServices.Ref) AND (&UseCharacteristics) AND (ProductsAndServices.UseCharacteristics) WHERE ProductsAndServices.ProductsAndServicesType IN(&PermittedProductsAndServicesTypes) {WHERE ProductsAndServices.Ref.* AS ProductsAndServices}
UNION
SELECT ProductsAndServices.Ref, VALUE(Catalog.ProductsAndServicesCharacteristics.EmptyRef), ProductsAndServices.DeletionMark, ProductsAndServices.Parent, ProductsAndServices.IsFolder, ProductsAndServices.Code, ProductsAndServices.Description, ProductsAndServices.SKU, ProductsAndServices.ProductsAndServicesType, ProductsAndServices.MeasurementUnit, ProductsAndServices.VATRate, ProductsAndServices.PictureFile, NULL, NULL FROM Catalog.ProductsAndServices AS ProductsAndServices {LEFT JOIN Catalog.ProductsAndServices.AdditionalAttributes AS ProductsAndServicesAdditionalAttributes ON ProductsAndServices.Ref = ProductsAndServicesAdditionalAttributes.Ref} WHERE Not ProductsAndServices.IsFolder AND ProductsAndServices.ProductsAndServicesType IN(&PermittedProductsAndServicesTypes) {WHERE ProductsAndServices.Ref.* AS ProductsAndServices}
INDEX BY ProductsAndServices, Characteristic ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED TemporaryTableProductsAndServicesAndCharacteristics.ProductsAndServices AS ProductsAndServices, TemporaryTableProductsAndServicesAndCharacteristics.Characteristic AS Characteristic, TemporaryTableProductsAndServicesAndCharacteristics.DeletionMark AS DeletionMark, TemporaryTableProductsAndServicesAndCharacteristics.Parent AS Parent, TemporaryTableProductsAndServicesAndCharacteristics.IsFolder AS IsFolder, TemporaryTableProductsAndServicesAndCharacteristics.Code AS Code, TemporaryTableProductsAndServicesAndCharacteristics.Description AS Description, TemporaryTableProductsAndServicesAndCharacteristics.SKU AS SKU, TemporaryTableProductsAndServicesAndCharacteristics.ProductsAndServicesType AS ProductsAndServicesType, TemporaryTableProductsAndServicesAndCharacteristics.MeasurementUnit AS MeasurementUnit, TemporaryTableProductsAndServicesAndCharacteristics.MeasurementUnit.Code AS MeasurementUnitCode, TemporaryTableProductsAndServicesAndCharacteristics.MeasurementUnit.DescriptionFull AS MeasurementUnitDescriptionFull, TemporaryTableProductsAndServicesAndCharacteristics.MeasurementUnit.InternationalAbbreviation AS MeasurementUnitInternationalAbbreviation, TemporaryTableProductsAndServicesAndCharacteristics.VATRate AS VATRate, TemporaryTableProductsAndServicesAndCharacteristics.PictureFile AS PictureFile, TemporaryTableProductsAndServicesAndCharacteristics.CharacteristicDescription AS CharacteristicDescription, TemporaryTableProductsAndServicesAndCharacteristics.CharacteristicDeletionMark AS CharacteristicDeletionMark, CASE WHEN TemporaryTableProductsAndServicesAndCharacteristics.ProductsAndServicesType = VALUE(Enum.ProductsAndServicesTypes.InventoryItem) THEN "Product" ELSE "Service" END AS ProductsAndServicesKind, ISNULL(TemporaryTableAvailableBallance.QuantityBalance, 0) AS Balance INTO TemporaryTableProductsAndServices FROM TemporaryTableProductsAndServicesAndCharacteristics AS TemporaryTableProductsAndServicesAndCharacteristics LEFT JOIN TemporaryTableAvailableBallance AS TemporaryTableAvailableBallance ON TemporaryTableProductsAndServicesAndCharacteristics.ProductsAndServices = TemporaryTableAvailableBallance.ProductsAndServices AND TemporaryTableProductsAndServicesAndCharacteristics.Characteristic = TemporaryTableAvailableBallance.Characteristic ;
//////////////////////////////////////////////////////////////////////////////// DROP TemporaryTableProductsAndServicesAndCharacteristics ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED TemporaryTableProductsAndServices.ProductsAndServices AS ProductsAndServices, MAX(ProductsAndServicesBarcodes.Barcode) AS Barcode INTO TemporaryTableBarcodesForDirectory FROM TemporaryTableProductsAndServices AS TemporaryTableProductsAndServices INNER JOIN InformationRegister.ProductsAndServicesBarcodes AS ProductsAndServicesBarcodes ON TemporaryTableProductsAndServices.ProductsAndServices = ProductsAndServicesBarcodes.ProductsAndServices AND (ProductsAndServicesBarcodes.Characteristic = VALUE(Catalog.ProductsAndServicesCharacteristics.EmptyRef))
GROUP BY TemporaryTableProductsAndServices.ProductsAndServices ;
//////////////////////////////////////////////////////////////////////////////// SELECT TemporaryTableProductsAndServices.ProductsAndServices AS ProductsAndServices, TemporaryTableProductsAndServices.DeletionMark AS DeletionMark, TemporaryTableProductsAndServices.Parent AS Parent, TemporaryTableProductsAndServices.Code AS Code, TemporaryTableProductsAndServices.Description AS Description, TemporaryTableProductsAndServices.ProductsAndServices.DescriptionFull AS DescriptionFull, TemporaryTableProductsAndServices.ProductsAndServices.Comment AS Comment, TemporaryTableProductsAndServices.SKU AS SKU, TemporaryTableProductsAndServices.ProductsAndServicesKind AS ProductsAndServicesKind, TemporaryTableProductsAndServices.ProductsAndServicesType AS ProductsAndServicesType, TemporaryTableProductsAndServices.MeasurementUnit AS MeasurementUnit, TemporaryTableProductsAndServices.MeasurementUnitCode AS MeasurementUnitCode, TemporaryTableProductsAndServices.MeasurementUnitDescriptionFull AS MeasurementUnitDescriptionFull, TemporaryTableProductsAndServices.MeasurementUnitInternationalAbbreviation AS MeasurementUnitInternationalAbbreviation, TemporaryTableProductsAndServices.VATRate AS VATRate, TemporaryTableProductsAndServices.PictureFile AS PictureFile, TemporaryTableProductsAndServices.Characteristic AS Characteristic, TemporaryTableProductsAndServices.CharacteristicDescription AS CharacteristicDescription, TemporaryTableProductsAndServices.CharacteristicDeletionMark AS CharacteristicDeletionMark, ISNULL(TemporaryTableBarcodesForDirectory.Barcode, """") AS Barcode, TemporaryTableProductsAndServices.Balance AS Balance FROM TemporaryTableProductsAndServices AS TemporaryTableProductsAndServices LEFT JOIN TemporaryTableBarcodesForDirectory AS TemporaryTableBarcodesForDirectory ON TemporaryTableProductsAndServices.ProductsAndServices = TemporaryTableBarcodesForDirectory.ProductsAndServices
ORDER BY ProductsAndServices, Characteristic
